import React, { useState } from 'react';
import './CreateEvent.css'; // Import the CSS file for styling

const CreateEvent = () => {
    const [eventDetails, setEventDetails] = useState({
        title: '',
        date: '',
        time: '',
        location: '',
        price: '',
        availableTickets: ''
    });

    const handleChange = (e) => {
        const { name, value } = e.target;
        setEventDetails({ ...eventDetails, [name]: value });
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        try {
            const response = await fetch('http://localhost:5000/api/events', {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json'
                },
                body: JSON.stringify(eventDetails)
            });
            if (response.ok) {
                alert('Event created successfully!');
                setEventDetails({
                    title: '',
                    date: '',
                    time: '',
                    location: '',
                    price: '',
                    availableTickets: ''
                });
            } else {
                alert('Error creating event.');
            }
        } catch (error) {
            console.error('Error:', error);
            alert('Error creating event.');
        }
    };

    return (
        <div className="create-event-container">
            <h1>Create an Event</h1>
            <form onSubmit={handleSubmit}>
                <input type="text" name="title" placeholder="Event Title" value={eventDetails.title} onChange={handleChange} required />
                <input type="date" name="date" value={eventDetails.date} onChange={handleChange} required />
                <input type="time" name="time" value={eventDetails.time} onChange={handleChange} required />
                <input type="text" name="location" placeholder="Location" value={eventDetails.location} onChange={handleChange} required />
                <input type="number" name="price" placeholder="Price" value={eventDetails.price} onChange={handleChange} required />
                <input type="number" name="availableTickets" placeholder="Available Tickets" value={eventDetails.availableTickets} onChange={handleChange} required />
                <button type="submit">Create Event</button>
            </form>
        </div>
    );
};

export default CreateEvent;
